import React from 'react'

import './module-random.css'
import Module from '../module'
import WebSocketService from '../../../services/websocket'
import {buf2hex} from '../../../utils/buffer'

const REFRESH_RANDOM_INTERVAL = 30 * 1000;
const REFRESH_COUNTER_INTERVAL = 100;

/**
 * This module will ask for a random number every fixed interval and display the result
 *
 * The count down is shown with a kind of loading bar
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export default class ModuleRandom extends React.Component {

  /**
   * @constructor
   * @param props
   */
  constructor(props) {
    super(props);

    this.state = {
      random: ''
    };
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   */
  componentWillMount() {
    this._triggerRandomUpdate();
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   * @returns {XML}
   */
  render() {
    const {random, counter} = this.state;

    return (
      <Module title="Random" icon="random" className="module-random">
        <strong>Random number</strong><br/>
        {random}

        <div className="module-random-counter">
          <div style={{width: `${counter * 100}%`}}/>
        </div>
      </Module>
    );
  }

  /**
   * Send a random request to the server and update the state with the result number
   * @private
   */
  _triggerRandomUpdate() {
    // todo fix servers
    WebSocketService.getRandom('192.33.210.8:7021').then(msg => {
      this._timestamp = Date.now();
      this.setState({
        random: buf2hex(msg.R),
        counter: 0
      });

      const self = this;
      setTimeout(() => self._checkCountDown(), REFRESH_COUNTER_INTERVAL);
    });

    this._timestamp = Date.now();
    this.setState({
      random: `${Math.round(Math.random() * Math.pow(10, 20))}`,
      counter: 0
    });

    const self = this;
    setTimeout(() => self._checkCountDown(), REFRESH_COUNTER_INTERVAL);
  }

  /**
   * This function will recursively call itself until the counter is reached to update the loading
   * animation. When the counter reached 1, it will trigger an update
   * @private
   */
  _checkCountDown() {
    // counter from 0 to 1
    const counter = Math.min(1, (Date.now() - this._timestamp) / REFRESH_RANDOM_INTERVAL);

    this.setState({counter});

    if (counter < 1) {
      setTimeout(() => this._checkCountDown(), REFRESH_COUNTER_INTERVAL);
    }
    else {
      this._triggerRandomUpdate();
    }
  }

}

import React from 'react'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group'

import Module from '../module'
import FileDropArea from './utils/drop-file-area'
import SignArea from './utils/sign-area'

/**
 * Module to upload a file and sign it using the current skipchain
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export default class ModuleSign extends React.Component {

  /**
   * @constructor
   * @param props
   */
  constructor(props) {
    super(props);

    this.state = {
      file: undefined
    };

    this.handleFileDropped = this.handleFileDropped.bind(this);
    this.handleBack = this.handleBack.bind(this);
  }

  /**
   * Change the file we want to sign
   * @param {File} file
   */
  handleFileDropped(file) {
    this.setState({
      file
    });
  }

  /**
   * Cancel the current file
   */
  handleBack() {
    this.setState({file: undefined});
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   * @returns {XML}
   */
  render() {
    const {file} = this.state;
    const body = file ?
      <SignArea key="sign-area" file={file} onBack={this.handleBack}/> :
      <FileDropArea key="file-drop-area" onFileDrop={this.handleFileDropped}/>;

    return (
      <Module className="module-sign" title="Sign a file" icon="lock">
        <ReactCSSTransitionGroup transitionName="module"
                                 transitionLeaveTimeout={1}
                                 transitionEnterTimeout={300}>
          {body}
        </ReactCSSTransitionGroup>
      </Module>
    );
  }
}

import React from 'react'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group'
import {Row, Col, Form, FormGroup, Label, Input} from 'reactstrap'

import './module-verify.css'
import Module from '../module'
import DropFileArea from './utils/drop-file-area'
import GenesisService from '../../../services/genesis'
import SignatureFile from '../../../models/signature-file'
import {readAsString, hashFile} from '../../../utils/file'
import {buf2hex} from '../../../utils/buffer'
import {tcp2ws} from '../../../utils/network'

/**
 * Module to verify a given signature file and the target file
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export default class ModuleVerify extends React.Component {

  /**
   * @constructor
   * @param props
   */
  constructor(props) {
    super(props);

    this.state = {
      file: undefined,
      isVerified: false,
      isHashCorrect: false,
      isSignatureCorrect: false,
      error: ''
    };

    this.handleFileDrop = this.handleFileDrop.bind(this);
    this.handleFileReset = this.handleFileReset.bind(this);
  }

  /**
   * Handle a file dropped in the drop area
   * @param {File} fileDropped
   */
  handleFileDrop(fileDropped) {
    const {file, isVerified} = this.state;

    if (!file || (!!file && isVerified)) {
      // Either the file hasn't been uploaded yet or we have already verified the previous one
      this.setState({
        error: '',
        file: fileDropped,
        isVerified: false,
        isSignatureCorrect: false
      });

      return;
    }

    return this._verifySignature(fileDropped).then(
      (state) => {
        this.setState(Object.assign(state, {file: undefined}));
      },
      (e) => {
        this.setState({
          file: undefined,
          error: e.message
        });
      }
    );
  }

  /**
   * Reset the current state to be able to change the file selected
   */
  handleFileReset() {
    this.setState({
      file: undefined,
      isVerified: false,
      isSignatureCorrect: false,
      isHashCorrect: false
    });
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   * @returns {XML}
   */
  render() {
    const feedback = this._generateFeedback();

    return (
      <Module className="module-verify" title="Verify" icon="unlock">
        <Row>
          <Col xs="6"><DropFileArea onFileDrop={this.handleFileDrop}/></Col>
          <Col xs="6">
            <div className="module-verify-feedback">
              <ReactCSSTransitionGroup transitionName="feedback"
                                       transitionLeaveTimeout={1}
                                       transitionEnterTimeout={300}>
                {feedback}
              </ReactCSSTransitionGroup>
            </div>
          </Col>
        </Row>
      </Module>
    );
  }

  /**
   * Return the current status of the verifying process
   * @returns {XML}
   * @private
   */
  _generateFeedback() {
    const {file, isVerified, isSignatureCorrect, isHashCorrect, error} = this.state;

    let errorMessage = null;
    if (error.length > 0) {
      errorMessage = <p key="step-error" className="has-error">{error}</p>
    }

    const result = !isVerified ? errorMessage : (
        <FormGroup>
          <Label>
            Hash: <strong>{isHashCorrect ? 'verified' : <span className="has-error">wrong</span>}</strong>
          </Label><br/>
          <Label>
            Signature: <strong>{isSignatureCorrect ? 'verified' : <span className="has-error">wrong</span>}</strong>
          </Label>

          {errorMessage}
        </FormGroup>
      );

    return (
      <Form>
        <FormGroup check>
          <Label check onClick={this.handleFileReset}>
            <Input type="radio" name="step" checked={!file} readOnly/>
            Upload the file
          </Label>
        </FormGroup>
        <FormGroup className={!file ? 'disable' : ''} check>
          <Label check>
            <Input type="radio" name="step" checked={!!file} readOnly/>
            Upload the signature
          </Label>
        </FormGroup>
        {result}
      </Form>
    );
  }

  /**
   * Check the signature
   * @param infoFile {File}
   * @private
   */
  _verifySignature(infoFile) {
    const {file} = this.state;

    return new Promise((resolve, reject) => {
      readAsString(infoFile)
        .then(
          (info) => {
            const signFile = new SignatureFile();
            try {
              signFile.parse(info);
            }
            catch (e) {
              reject(e);
              return;
            }

            GenesisService.getLatestFromGenesisID(signFile.getGenesisID(true), signFile.getBlockID(true))
              .then((block) => {
                const pubkey = cryptoJS.aggregateKeys( // eslint-disable-line
                  block.Roster.list
                    .filter(s => signFile.getOfflineServers().every(off => tcp2ws(s.address).indexOf(off) === -1))
                    .map(s => s.public)
                );

                hashFile(file).then(
                  (hash) => {
                    resolve({
                      error: '',
                      isVerified: true,
                      isHashCorrect: buf2hex(hash) === signFile.getHash(true),
                      isSignatureCorrect: cryptoJS.verify(pubkey, hash, signFile.getSignature()) // eslint-disable-line
                    });
                  }
                );
              })
              .catch((e) => reject(e));
          }
        );
    });
  }
}

import React, {PropTypes as T} from 'react'
import FontAwesome from 'react-fontawesome'

import './drop-file-area.css'

/**
 * This component is used to drop a file or upload a file from the user's computer
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export default class DropFileArea extends React.Component {

  static propTypes = {
    onFileDrop: T.func
  };

  /**
   * @constructor
   * @param props
   */
  constructor(props) {
    super(props);

    this.handleClick = this.handleClick.bind(this);
    this.handleUpload = this.handleUpload.bind(this);
    this.handleDrop = this.handleDrop.bind(this);
  }

  /**
   * Bubble the click of the user to the hidden input
   */
  handleClick() {
    this.refs['file-input'].click();
  }

  /**
   * Trigger the file upload event
   * @param {Event} e
   */
  handleUpload(e) {
    this._handleFileDropped(e.target.files);
  }

  /**
   * Trigger the file upload event
   * @param {Event} e
   */
  handleDrop(e) {
    e.preventDefault();

    this._handleFileDropped(e.dataTransfer.files);
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   * @returns {XML}
   */
  render() {
    return (
      <div className="drop-file-area">
        <input type="file" ref="file-input" style={{display: "none"}} onChange={this.handleUpload}/>

        <div className="drop-file-container"
             onClick={this.handleClick}
             onDrop={this.handleDrop}
             onDragOver={(e) => e.preventDefault()}>

          <FontAwesome name="download"/><br/>
          Click or drop a file
        </div>
      </div>
    );
  }

  /**
   * Trigger the file dropped event with the given file
   * @param {Array} files
   * @private
   */
  _handleFileDropped(files) {
    const {onFileDrop} = this.props;

    if (files.length > 0 && typeof onFileDrop === 'function') {
      onFileDrop(files[0]);
    }
  }
}

import React, {PropTypes as T} from 'react'
import {Button} from 'reactstrap'
import FontAwesome from 'react-fontawesome'

import './sign-area.css'
import {hashFile} from '../../../../utils/file'
import {tcp2ws} from '../../../../utils/network'
import CothorityWebsocket from '../../../../services/websocket'
import GenesisService from '../../../../services/genesis'
import StatusService from '../../../../services/status'
import SignatureFile from '../../../../models/signature-file'

/**
 * Show the status of the sign process
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export default class SignArea extends React.Component {

  static propTypes = {
    file: T.instanceOf(File).isRequired,
    onBack: T.func
  };

  /**
   * @constructor
   * @param props
   */
  constructor(props) {
    super(props);

    this.state = {
      isSigning: false,
      error: '',

      latestBlock: null,
      genesisID: ''
    };

    this.handleBackAction = this.handleBackAction.bind(this);
    this.handleSign = this.handleSign.bind(this);
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   */
  componentWillMount() {
    GenesisService.subscribe(this);
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   */
  componentWillUnmount() {
    GenesisService.unsubscribe(this);
  }

  /**
   * @see GenesisService._triggerEvent
   * @param {Array} blocks
   * @param {Array} genesisList
   * @param {String} currGenesis
   */
  onGenesisUpdate(blocks, genesisList, currGenesis) {
    this.setState({
      genesisID: currGenesis,
      block: blocks.slice().pop()
    });
  }

  /**
   * @see GenesisService._triggerError
   * @param {Error} error
   */
  onGenesisError(error) {
    this.setState({
      error: error.message
    });
  }

  /**
   * Return to the drop file component
   */
  handleBackAction() {
    this._triggerOnBack();
  }

  /**
   * Send a sign request over WS with the current loaded file
   */
  handleSign() {
    this.setState({
      isSigning: true
    });

    const {file} = this.props;
    // We assume we have a valid genesis ID and the associated block
    // else an error will be displayed before letting the user sign
    const {genesisID, block} = this.state;

    const servers = StatusService.getAvailableRoster().filter(r => !!r.system).map(r => r.server);

    const signFile = new SignatureFile();
    signFile.setFileName(file.name);
    signFile.setGenesisID(genesisID);
    signFile.setBlockID(block.Hash);
    signFile.setOfflineServers(StatusService.getAvailableRoster().filter(r => !r.system).map(r => r.server.address));

    this._signPromise = new Promise((resolve, reject) => {
      // Check if more than 2/3 of the servers are available
      if (servers.length / block.Roster.list.length < 2 / 3) {
        reject("Not enough available servers");
        return;
      }

      hashFile(file)
        .then(
          (hash) => {
            signFile.setHash(hash);
            const address = tcp2ws(servers[0].address);

            if (address.length > 0) {
              CothorityWebsocket.getSignature(hash, address, servers)
                .then(
                  (response) => {
                    console.log(response);
                    const signature = (response.signature || []).slice(0, 64);
                    signFile.setSignature(signature);

                    signFile.save();
                    this._triggerOnBack();

                    resolve();
                  }
                )
                .catch((e) => {
                  console.log(e);
                  reject('Oops, something went wrong...');
                })
            }
          }
        );
    });

    // We want to display the error message if it occurs
    this._signPromise.catch(e => this.setState({error: e}));
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   * @returns {XML}
   */
  render() {
    const {error} = this.state;
    if (error.length > 0) {
      return (
        <div className="sign-area">
          <div className="sign-area-error">{error}</div>
          <div className="sign-area-action">
            <Button color="danger" onClick={this.handleBackAction}>Ok</Button>
          </div>
        </div>
      );
    }

    const {file} = this.props;
    const size = (file.size / (1024 * 1024)).toFixed(2);

    const {isSigning} = this.state;
    const signAction = isSigning ? <div className="loading"><FontAwesome name="circle-o-notch" size="2x" spin/></div> :
      <Button color="success" onClick={this.handleSign}>Sign</Button>;

    return (
      <div className="sign-area">
        <div className="sign-area-file">
          <div><strong>Name</strong> {file.name}</div>
          <div><strong>Size</strong> {size}MB</div>
        </div>
        <div className="sign-area-action">
          <Button color="danger" onClick={this.handleBackAction}>Back</Button>
          {signAction}
        </div>
      </div>
    );
  }

  /**
   * Trigger the back event
   * @private
   */
  _triggerOnBack() {
    const {onBack} = this.props;
    if (typeof onBack === 'function') {
      onBack();
    }
  }
}

import React from 'react'
import Module from '../module'
import ByteBuffer from 'bytebuffer'
import {FormGroup, Label, Input} from 'reactstrap'
import {Scrollbars} from 'react-custom-scrollbars';

import './module-skipchain.css'
import GenesisService from '../../../services/genesis'

/**
 * This module displays the list of the available skipchains except the ones
 * that contains an URL in the data
 *
 * @author Gaylor Bosson (gaylor.bosson@dmetrics.com)
 */
export default class ModuleSkipChain extends React.Component {

  /**
   * @constructor
   * @param props
   */
  constructor(props) {
    super(props);

    this.state = {
      genesisList: [],
      currGenesis: ''
    };

    this.handleGenesisChange = this.handleGenesisChange.bind(this);
  }

  /**
   * It will update the state with the current skipchain
   * @param {Array} blocks
   * @param {Array} genesisList
   * @param {String} curr_genesis
   */
  onGenesisUpdate(blocks, genesisList, curr_genesis) {
    this.setState({
      blocks,
      genesisList: genesisList.filter(b => {
        if (b.Data) {
          const data = ByteBuffer.fromBase64(b.Data);
          if (data.capacity() > 18) {
            return !data.slice(18).toString('utf8').match(/^https?:\/\//);
          }
        }

        return true;
      }),
      currGenesis: curr_genesis
    });
  }

  /**
   * Update the state to display the error
   * @param {Error} error
   */
  onGenesisError(error) {
    this.setState({error});
  }

  /**
   * It will trigger the skipchain change to the genesis service
   * @param {String} id - the new genesis block
   */
  handleGenesisChange(id) {
    const {currGenesis} = this.state;

    if (id !== currGenesis) {
      GenesisService.setCurrentGenesisID(id);
    }
  }

  /**
   * @override
   * @see https://facebook.github.io/react/docs/react-component.html
   */
