/**
 * Translate char by char a sequence of hex digit to UTF-8 digit
 * @param str
 * @returns {string}
 */
function hexToUTF8(str) {
  const hexes = str.match(/.{1,2}/g) || [];
  let back = "";
  for (let j = 0; j < hexes.length; j++) {
    back += String.fromCharCode(parseInt(hexes[j], 16));
  }

  return back;
}

