const REFRESH_INTERVAL = 30000;

/**
 * This service takes care of keeping the status of the roster of the current skipchain up-to-date
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export class StatusService {

  /**
   * Subscribe the the genesis service
   * @constructor
   */
  constructor() {
    this.status = {};
    this.listeners = [];
    this.servers = [];
    this.genesisList = [];
    this.refreshInterval = REFRESH_INTERVAL;

    GenesisService.subscribe(this);
  }

  /**
   * On a new skipchain it resets the status and fetch the new roster
   * @param {Array} blocks
   * @param {Array} genesisList
   * @see GenesisService._triggerEvent
   */
  onGenesisUpdate(blocks, genesisList) {
    if (blocks.length === 0) {
      return;
    }

    this.status = {};
    this.servers = blocks.slice().pop().Roster.list.map(block => tcp2ws(block.address));
    this.genesisList = genesisList;

    this._updateStatus();
  }

  /**
   * Send the new status to listeners
   */
  triggerUpdate() {
    this.listeners.forEach((listener) => {
      this._triggerEvent(listener);
    });
  }

  /**
   * Add the given object to the list of listeners
   * @param {Object} listener
   */
  subscribe(listener) {
    if (this.listeners.indexOf(listener) === -1) {
      this.listeners.push(listener);

      this._triggerEvent(listener);
    }
  }

  /**
   * Remove the listener from the list
   * @param {object} listener
   */
  unsubscribe(listener) {
    const index = this.listeners.indexOf(listener);
    if (index >= 0) {
      this.listeners.splice(index, 1);
    }
  }

  /**
   * Return the list of server that are responding
   * Check the protobuf definition of a Status message for the return array
   * @returns {Array<Status>}
   */
  getAvailableRoster() {
    return Object.keys(this.status).map(key => {
      return this.status[key];
    });
  }

  /**
   * Maintain a timeout to fetch every given interval the status of the servers
   * @private
   */
  _updateStatus() {
    if (this._timer) {
      clearTimeout(this._timer);
      delete this._timer;
    }

    const self = this;

    this.servers.forEach((address, i) => CothorityWS.getStatus(address)
      .then((response) => {
        response.timestamp = Date.now(); // add the timestamp of the last check
        self.status[address] = response;
        self.triggerUpdate();
      })
      .catch(() => {
        self.status[address] = {
          timestamp: Date.now(),
          server: {address}
        };
        self.triggerUpdate();
      })
    );

    this._timer = setTimeout(() => self._updateStatus(), self.refreshInterval);
  }

  /**
   * Send the status to the given listener
   * @param {Object} listener
   * @private
   */
  _triggerEvent(listener) {
    if (typeof listener.onStatusUpdate === 'function') {
      listener.onStatusUpdate(this.status, this.genesisList);
    }
  }
}

export default new StatusService();

import CothorityMessages from '../lib/cothority-messages'

const EMPTY_MESSAGE = new Uint8Array([]);

/**
 * This service maintains or create websockets for the different actions the front-end can perform
 *
 * @author Gaylor Bosson (gaylor.bosson@epfl.ch)
 */
export class CothorityWebsocket {

  /**
   * @constructor
   */
  constructor() {
    this.status = {};
  }

  /**
   * Get the status of the given server among a WS
   * @param {String} address - ip address
   * @returns {Promise}
   */
  getStatus(address) {

    // The promise will resolve when the socket sends back the response of the call
    return new Promise((resolve, reject) => {
      this.status[address] = createSocket(
        this.status[address],
        address + '/Status/Request',
        (e) => reject(e),
        (data) => resolve(CothorityMessages.decodeStatusResponse(data)),
        EMPTY_MESSAGE
      );
    });

  }

  /**
   * Get the signature of the given hash with the given roster
   * @param {Uint8Array} hash - hash of the message
   * @param {String} address - ip address
   * @param {Roster} roster - check protobuf Roster message
   * @returns {Promise}
   */
  getSignature(hash, address, roster) {

    return new Promise((resolve, reject) => {
      this.sign_socket = createSocket(
        this.sign_socket,
        address + '/CoSi/SignatureRequest',
        (e) => reject(e),
        (data) => resolve(CothorityMessages.decodeSignatureResponse(data)),
        CothorityMessages.createSignatureRequest(hash, roster)
      );
    });

  }

  /**
   * Get the list of blocks of the skipchain with the given genesis ID
   * @param {String} address - ip address
   * @param {Uint8Array} id - Genesis ID
   * @returns {Promise}
   */
  getLatestBlock(address, id) {

    return new Promise((resolve, reject) => {
      this.latest_block = createSocket(
        this.latest_block,
        address + '/Skipchain/GetBlocks',
        (e) => reject(e),
        (data) => resolve(CothorityMessages.decodeLatestBlockResponse(data)),
        CothorityMessages.createLatestBlockRequest(id)
      )
    });

  }

  /**
   * Store a new block with the given roster
   * @param {String} address - ip address
   * @param {Uint8Array} id - Genesis ID
   * @param {Array} servers - list of servers
   * @returns {Promise}
   */
  storeNewBlock(address, id, servers) {

    return new Promise((resolve, reject) => {
      this.store_block = createSocket(
        this.store_block,
        address + '/Skipchain/StoreSkipBlock',
        (e) => reject(e),
        (data) => resolve(CothorityMessages.decodeStoreSkipBlockResponse(data)),
        CothorityMessages.createStoreSkipBlockRequest(id, servers)
      )
    });

  }

  /**
   * Get a random number from address
   * @param {String} address - ip address
   * @returns {Promise}
   */
  getRandom(address) {
    return new Promise((resolve, reject) => {
      this.random = createSocket(
        this.random,
        address + '/RandHound/Random',
        (e) => reject(e),
        (data) => resolve(CothorityMessages.decodeRandomResponse(data)),
        CothorityMessages.createRandomMessage()
      )
    });
  }

}

export default new CothorityWebsocket();

/**
 * Use the existing socket or create a new one if required
 * @param socket - WebSocket old socket
 * @param address - String ws address
 * @param error - Function callback if an error occurred
 * @param callback - Function callback when a message is received
 * @param message - ArrayBuffer the message to send
 * @returns {*}
 */
function createSocket(socket, address, error, callback, message) {
  if (!socket || socket.readyState > 2) {
    socket = new WebSocket(`ws://${address}`);
    socket.binaryType = 'arraybuffer';
  }

  function onError(e) {
    socket.removeEventListener('error', onError);
    error(e);
  }
  socket.addEventListener('error', onError);

  function onMessage(e) {
    socket.removeEventListener('message', onMessage);
    callback(e.data);
  }
  socket.addEventListener('message', onMessage);

  if (socket.readyState === 0) {
    socket.addEventListener('open',() => {
      socket.send(message);
    });
  }
  else {
    socket.send(message);
  }

  return socket;
}

import ByteBuffer from 'bytebuffer'

/**
 * Given a hexadecimal string it converts it to a Uint8Array buffer
 * @param {String} hex
 * @returns {Uint8Array}
 */
export function hex2buf(hex) {
  return new Uint8Array(ByteBuffer.fromHex(hex).buffer);
}

/**
 * Given a buffer, convert it to an hexadecimal string
 * @param {Uint8Array} buffer
 * @returns {String}
 */
export function buf2hex(buffer) {
  if (!buffer) {
    return '';
  }

  return ByteBuffer.wrap(buffer).toHex();
}

/**
 * Read the given file object and return a promise that will resolve with the hash of the file
 * @param {File} file
 */
export function hashFile(file) {

  return new Promise((resolve) => {
    const reader = new FileReader();

    reader.onload = (e) => {
      const buf = new Uint8Array(e.target.result);
      resolve(cryptoJS.sha256(buf)); // eslint-disable-line
    };
    reader.readAsArrayBuffer(file);
  });

}

/**
 * Read a file as a string
 * @param {File} file
 * @returns {Promise}
 */
export function readAsString(file) {

  return new Promise((resolve) => {
    const reader = new FileReader();

    reader.onload = (e) => {
      resolve(e.target.result);
    };
    reader.readAsText(file);
  });

}

/**
 * Transform a tcp address to the corresponding WS address because the Cothority uses the port + 1 of the tcp address
 *
 * @param {String} address - TCP with port address format
 * @returns {String} The same address with the port value +1
 */
export function tcp2ws(address) {
  const addr = address.match(/([0-9]{1,3}\.){3}[0-9]{1,3}/)[0];
  const port = Number(address.match(/[0-9]{1,6}$/)[0]);

  return addr + ':' + (port + 1);
}
